import { useState } from "react";

const PlaylistSearch = ({ accessToken, onSelectPlaylist }) => {
  const [query, setQuery] = useState("");
  const [playlists, setPlaylists] = useState([]);

  const searchPlaylists = async () => {
    if (!query) return;
    const res = await fetch(
      `https://api.spotify.com/v1/search?q=${encodeURIComponent(query)}&type=playlist&limit=5`,
      { headers: { Authorization: `Bearer ${accessToken}` } }
    );
    const data = await res.json();
    setPlaylists(data.playlists.items);
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Playlist suchen..."
      />
      <button onClick={searchPlaylists}>üîç</button>

      <select onChange={(e) => onSelectPlaylist(e.target.value)}>
        <option value="">W√§hle eine Playlist</option>
        {playlists.map((pl) => (
          <option key={pl.id} value={pl.id}>
            {pl.name}
          </option>
        ))}
      </select>
    </div>
  );
};

export default PlaylistSearch;
