import React, { useEffect, useState } from "react";

const SpotifyPlayer = ({ accessToken, track }) => {
  const [deviceId, setDeviceId] = useState(null);
  const [player, setPlayer] = useState(null);

  useEffect(() => {
    if (!accessToken) return;

    const script = document.createElement("script");
    script.src = "https://sdk.scdn.co/spotify-player.js";
    script.async = true;
    document.body.appendChild(script);

    window.onSpotifyWebPlaybackSDKReady = () => {
      const newPlayer = new window.Spotify.Player({
        name: "Spotify Song Quiz",
        getOAuthToken: (cb) => cb(accessToken),
        volume: 0.5,
      });

      newPlayer.addListener("ready", ({ device_id }) => {
        console.log("‚úÖ Spotify Player bereit, Device ID:", device_id);
        setDeviceId(device_id);
      });

      newPlayer.addListener("not_ready", ({ device_id }) => {
        console.log("‚ùå Spotify Player nicht bereit, Device ID:", device_id);
      });

      newPlayer.connect();
      setPlayer(newPlayer);
    };

    return () => {
      if (player) player.disconnect();
    };
  }, [accessToken]);

  const playTrack = async () => {
    if (!deviceId || !track) {
      console.error("‚ùå Kein Ger√§t oder Track gefunden!");
      return;
    }

    try {
      await fetch("https://api.spotify.com/v1/me/player/play", {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ uris: [track.uri] }),
      });
      console.log("üéµ Song wird abgespielt:", track.name);
    } catch (error) {
      console.error("‚ùå Fehler beim Abspielen des Songs:", error);
    }
  };

  return (
    <div>
      <button onClick={playTrack} disabled={!track}>
        üéµ Play {track ? track.name : ""}
      </button>
    </div>
  );
};

export default SpotifyPlayer;
