import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const navigate = useNavigate();

const SongQuiz = ({ accessToken, playlistId, deviceId }) => {
  const [songs, setSongs] = useState([]);
  const [currentSong, setCurrentSong] = useState(null);
  const [options, setOptions] = useState([]);
  const [feedback, setFeedback] = useState("");

  useEffect(() => {
    const fetchTracks = async () => {
      const res = await fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks`, {
        headers: { Authorization: `Bearer ${accessToken}` },
      });
      const data = await res.json();
      const tracks = data.items
        .map((item) => ({
          id: item.track.id,
          uri: item.track.uri,
          name: item.track.name,
        }))
        .filter((track) => track.id);
      setSongs(tracks);
      startNewRound(tracks);
    };

    if (playlistId) fetchTracks();
  }, [playlistId]);

  const startNewRound = (tracks) => {
    const shuffled = [...tracks].sort(() => 0.5 - Math.random());
    setCurrentSong(shuffled[0]);
    setOptions(shuffled.slice(0, 4));
  };

  const playSong = async () => {
    if (!deviceId || !currentSong) {
      console.error("âŒ Kein gÃ¼ltiges GerÃ¤t oder Song gefunden!");
      return;
    }

    console.log("ðŸŽµ Starte Song:", currentSong.name, "auf GerÃ¤t:", deviceId);

    try {
      const res = await fetch("https://api.spotify.com/v1/me/player/play", {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${accessToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ device_id: deviceId, uris: [currentSong.uri] }),
      });

      if (!res.ok) {
        console.error("âŒ Fehler beim Abspielen des Songs:", await res.text());
      } else {
        console.log("âœ… Song erfolgreich gestartet!");
      }
    } catch (error) {
      console.error("âŒ Fehler beim Starten des Songs:", error);
    }
  };

  useEffect(() => {
    if (currentSong) playSong();
  }, [currentSong]);

  const checkAnswer = (selectedTrack) => {
    if (selectedTrack.id === currentSong.id) {
      setFeedback("âœ… Richtig!");
    } else {
      setFeedback(`âŒ Falsch! Richtig wÃ¤re: ${currentSong.name}`);
    }
    setTimeout(() => startNewRound(songs), 2000);
  };
  const playRandomPart = async () => {
    if (!deviceId || !currentSong) return;

    const trackDurationMs = currentSong.duration_ms || 180000; // Falls nicht verfÃ¼gbar, default: 3min
    const randomStartMs = Math.floor(Math.random() * (trackDurationMs - 15000)); // Starte irgendwo im Song

    console.log(`ðŸŽµ Starte Song fÃ¼r 15 Sekunden ab: ${randomStartMs}ms`);

    await fetch("https://api.spotify.com/v1/me/player/play", {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        uris: [currentSong.uri],
        position_ms: randomStartMs,
      }),
    });

    // Stoppe nach 15 Sekunden automatisch
    setTimeout(() => {
      stopPlayback();
      setFeedback(`â³ Zeit abgelaufen! Richtige Antwort: ${currentSong.name}`);
      setTimeout(() => startNewRound(songs), 3000);
    }, 15000);
  };
  return (
    <div className="quiz-container">
        <button onClick={() => navigate("/")} className="back-button">
            â¬… ZurÃ¼ck zur Ãœbersicht
         </button>
      <h2>Errate den Song! ðŸŽµ</h2>
      <p>{feedback}</p>
      {options.map((track) => (
        <button key={track.id} onClick={() => checkAnswer(track)} className="quiz-button">
          {track.name}
        </button>
      ))}
    </div>

  );
};

export default SongQuiz;
