import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import TokenStatus from "./components/TokenStatus";
import LoginButton from "./components/LoginButton";
import QuizPage from "./pages/QuizPage";
import SongQuizPage from "./pages/SongQuizPage";
import AuthCallback from "./components/AuthCallback";
import "./styles/styles.css";
import Home from "./pages/Home";

const App = () => {
  const [accessToken, setAccessToken] = useState(localStorage.getItem("spotify_token") || "");

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get("access_token");

    if (token) {
      localStorage.setItem("spotify_token", token);
      setAccessToken(token);
    } else if (!accessToken) {
      refreshAccessToken();
    }
  }, []);

  const refreshAccessToken = async () => {
    try {
      const res = await fetch("http://localhost:5000/auth/refresh", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
      });

      const data = await res.json();
      if (data.access_token) {
        localStorage.setItem("spotify_token", data.access_token);
        setAccessToken(data.access_token);
      } else {
        console.error("❌ Kein neuer Token erhalten:", data);
      }
    } catch (err) {
      console.error("❌ Fehler beim Token-Refresh:", err);
    }
  };

  return (
    <Router>
      <div className="app-container">
        <TokenStatus accessToken={accessToken} refreshAccessToken={refreshAccessToken} />
        {!accessToken ? <LoginButton /> : null}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/quiz/:mode" element={<QuizPage />} />
          <Route path="/quiz/song" element={<SongQuizPage accessToken={accessToken} />} />
          <Route path="/auth/callback" element={<AuthCallback />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
