import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import TokenStatus from "./components/TokenStatus";
import "./styles/styles.css";

const App = () => {
  const [accessToken, setAccessToken] = useState(localStorage.getItem("spotify_token") || "");

  useEffect(() => {
    if (!accessToken) refreshAccessToken();
  }, []);

  const refreshAccessToken = async () => {
    try {
      const res = await fetch("http://localhost:5000/auth/refresh"); // Backend-Endpunkt für Token-Refresh
      const data = await res.json();
      if (data.access_token) {
        localStorage.setItem("spotify_token", data.access_token);
        setAccessToken(data.access_token);
      } else {
        console.error("❌ Fehler beim Token-Refresh:", data);
      }
    } catch (err) {
      console.error("❌ Fehler beim Abrufen des neuen Tokens:", err);
    }
  };

  return (
    <Router>
      <div className="app-container">
        <TokenStatus accessToken={accessToken} refreshAccessToken={refreshAccessToken} />
        <Routes>
      <Route path="/" element={<GameMode />} />
      <Route path="/quiz/:mode" element={<QuizPage />} />
      <Route path="/quiz/song" element={<SongQuizPage />} />
      <Route path="/auth/callback" element={<AuthCallback />} />
    </Routes>
      </div>
    </Router>
  );
};

export default App;
