const SPOTIFY_API_URL = "https://api.spotify.com/v1"; // API-URL als Konstante

export const activateSpotifyDevice = async (deviceId, accessToken) => {
  try {
    console.log("üéµ Versuche, den Spotify Web Player als aktives Ger√§t zu setzen...");

    const response = await fetch(`${SPOTIFY_API_URL}/me/player`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ device_ids: [deviceId], play: false }),
    });

    if (!response.ok) {
      console.error("‚ùå Fehler beim Aktivieren des Spotify-Ger√§ts:", await response.text());
    } else {
      console.log("‚úÖ Spotify Web Player erfolgreich als aktives Ger√§t gesetzt.");
    }
  } catch (error) {
    console.error("‚ùå Fehler beim Aktivieren des Ger√§ts:", error);
  }
};

export const startPlayback = async (deviceId, accessToken) => {
  try {
    console.log("‚ñ∂Ô∏è Versuche, die Wiedergabe zu starten...");

    const response = await fetch(`${SPOTIFY_API_URL}/me/player/play`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${accessToken}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ device_id: deviceId }),
    });

    if (!response.ok) {
      console.error("‚ùå Fehler beim Starten der Wiedergabe:", await response.text());
    } else {
      console.log("‚úÖ Wiedergabe erfolgreich gestartet!");
    }
  } catch (error) {
    console.error("‚ùå Fehler beim Starten der Wiedergabe:", error);
  }
};

export const checkActiveDevice = async (accessToken) => {
  try {
    const response = await fetch(`${SPOTIFY_API_URL}/me/player/devices`, {
      headers: { Authorization: `Bearer ${accessToken}` },
    });

    const data = await response.json();
    console.log("üîç Aktive Ger√§te:", data);

    if (data.devices.length === 0) {
      console.warn("‚ö†Ô∏è Kein aktives Ger√§t gefunden.");
    } else {
      console.log("‚úÖ Aktives Ger√§t:", data.devices.find(d => d.is_active));
    }
  } catch (error) {
    console.error("‚ùå Fehler beim Abrufen der Ger√§te:", error);
  }
};
